import styled from 'styled-components';

const Button = styled.button<{ color?: string }>`
  background-color: ${(props) => props.color || 'blue'};
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 3px;
  cursor: pointer;
  &:hover {
    background-color: darkblue;
  }
`;

export default {
    Button
};
